   329‚Üíexport function RequestGraph({ requests, selectedId, onSelect, getMessage }: RequestGraphProps) {
   330‚Üí  const tree = useMemo(() => buildRequestTree(requests), [requests]);
   331‚Üí  const flat = useMemo(() => buildFlatNodes(tree), [tree]);
   332‚Üí  const laneSpans = useMemo(() => buildLaneSpans(flat), [flat]);
   333‚Üí  const maxCol = useMemo(() => Math.max(...flat.map((n) => n.column), 0), [flat]);
   334‚Üí  const svgWidth = LEFT_PAD * 2 + (maxCol + 1) * COL_WIDTH;
   335‚Üí
   336‚Üí  if (flat.length === 0) {
   337‚Üí    return null;
   338‚Üí  }
   339‚Üí
   340‚Üí  return (
   341‚Üí    <div style={{ position: 'relative', width: '100%' }}>
   342‚Üí      <ConnectorLayer flat={flat} laneSpans={laneSpans} totalRows={flat.length} svgWidth={svgWidth} />
   343‚Üí      {flat.map((node) => (
   344‚Üí        <GraphRow
   345‚Üí          key={node.id}
   346‚Üí          node={node}
   347‚Üí          svgWidth={svgWidth}
   348‚Üí          isSelected={selectedId === node.id}
   349‚Üí          onClick={() => onSelect(node.id)}
   350‚Üí          getMessage={getMessage}
   351‚Üí        />
   352‚Üí      ))}
   353‚Üí    </div>
   354‚Üí  );
   355‚Üí}
