   246‚Üí            left: 0,
   247‚Üí            top: 0,
   248‚Üí            bottom: 0,
   249‚Üí            width: 2,
   250‚Üí            background: 'var(--color-border-accent)',
   251‚Üí          }}
   252‚Üí        />
   253‚Üí      )}
   254‚Üí
   255‚Üí      {/* Graph lane area */}
   256‚Üí      <div
   257‚Üí        style={{
   258‚Üí          position: 'relative',
   259‚Üí          width: svgWidth,
   260‚Üí          flexShrink: 0,
