   301‚Üí        {/* Line 2: Type, Model, Duration */}
   302‚Üí        <div className="flex items-center gap-2 text-xs text-text-muted">
   303‚Üí          <span className="shrink-0">{messageType}</span>
   304‚Üí          <span className="text-text-muted/50">|</span>
   305‚Üí          <span className="truncate">{request.model}</span>
   306‚Üí          <span className="text-text-muted/50">|</span>
   307‚Üí          <span
   308‚Üí            className={`shrink-0 ${
   309‚Üí              request.duration_ms > 5000 ? 'text-warning' : ''
   310‚Üí            }`}
   311‚Üí          >
   312‚Üí            {formatDuration(request.duration_ms)}
   313‚Üí          </span>
