     4‚Üí
     5‚Üí// ‚îÄ‚îÄ‚îÄ Constants ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ
     6‚Üí
     7‚Üíconst ROW_HEIGHT = 48;
     8‚Üíconst COL_WIDTH = 20;
     9‚Üíconst NODE_R = 5;
    10‚Üíconst LEFT_PAD = 14;
    11‚Üí
    12‚Üí// ‚îÄ‚îÄ‚îÄ Types ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ
    13‚Üí
    14‚Üíinterface FlatNode {
    15‚Üí  id: string;
    16‚Üí  request: Request;
    17‚Üí  column: number;
    18‚Üí  parentId: string | null;
    19‚Üí  isNewBranch: boolean;
    20‚Üí}
    21‚Üí
    22‚Üí// ‚îÄ‚îÄ‚îÄ Column Assignment (git-style) ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ
    23‚Üí
    24‚Üífunction assignCols(
    25‚Üí  node: RequestTreeNode,
    26‚Üí  col: number,
    27‚Üí  isNewBranch: boolean,
    28‚Üí  counter: { value: number },
    29‚Üí  parentId: string | null,
    30‚Üí  out: FlatNode[],
    31‚Üí): void {
    32‚Üí  out.push({
    33‚Üí    id: node.request.id,
    34‚Üí    request: node.request,
    35‚Üí    column: col,
    36‚Üí    parentId,
    37‚Üí    isNewBranch,
    38‚Üí  });
    39‚Üí
    40‚Üí  if (node.children.length > 0) {
    41‚Üí    node.children.forEach((child, i) => {
    42‚Üí      if (i === 0) {
    43‚Üí        assignCols(child, col, false, counter, node.request.id, out);
    44‚Üí      } else {
    45‚Üí        const newCol = counter.value++;
    46‚Üí        assignCols(child, newCol, true, counter, node.request.id, out);
    47‚Üí      }
    48‚Üí    });
    49‚Üí  }
    50‚Üí}
    51‚Üí
    52‚Üífunction buildFlatNodes(roots: RequestTreeNode[]): FlatNode[] {
    53‚Üí  const out: FlatNode[] = [];
    54‚Üí  const counter = { value: 1 };
    55‚Üí  roots.forEach((root, i) => {
    56‚Üí    const col = i === 0 ? 0 : counter.value++;
    57‚Üí    assignCols(root, col, i > 0, counter, null, out);
    58‚Üí  });
    59‚Üí  // Sort by timestamp for strict chronological order
    60‚Üí  out.sort((a, b) => a.request.timestamp - b.request.timestamp);
    61‚Üí  return out;
    62‚Üí}
    63‚Üí
    64‚Üí// ‚îÄ‚îÄ‚îÄ Lane Spans ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ
    65‚Üí
    66‚Üíinterface LaneSpan {
    67‚Üí  openY: number;
    68‚Üí  endY: number;
    69‚Üí}
    70‚Üí
    71‚Üífunction curveHeight(dx: number) {
    72‚Üí  return Math.max(ROW_HEIGHT, dx * 0.75);
    73‚Üí}
    74‚Üí
    75‚Üífunction colX(col: number) {
    76‚Üí  return LEFT_PAD + col * COL_WIDTH;
    77‚Üí}
    78‚Üí
    79‚Üífunction rowY(row: number) {
    80‚Üí  return row * ROW_HEIGHT + ROW_HEIGHT / 2;
    81‚Üí}
    82‚Üí
    83‚Üífunction buildLaneSpans(flat: FlatNode[]): Map<number, LaneSpan> {
    84‚Üí  const rowById = new Map<string, number>(flat.map((n, i) => [n.id, i]));
    85‚Üí  const spans = new Map<number, LaneSpan>();
    86‚Üí
    87‚Üí  flat.forEach((node, rowIdx) => {
    88‚Üí    const span = spans.get(node.column);
    89‚Üí    const nodeY = rowY(rowIdx);
    90‚Üí    const endY = span ? Math.max(span.endY, nodeY) : nodeY;
    91‚Üí
    92‚Üí    let openY: number;
    93‚Üí    if (node.isNewBranch && node.parentId !== null) {
    94‚Üí      const parentRowIdx = rowById.get(node.parentId) ?? rowIdx;
    95‚Üí      const parentNode = flat[parentRowIdx];
    96‚Üí      const dx = Math.abs(colX(node.column) - colX(parentNode.column));
    97‚Üí      openY = rowY(parentRowIdx) + curveHeight(dx);
    98‚Üí    } else {
    99‚Üí      openY = span ? Math.min(span.openY, nodeY) : nodeY;
   100‚Üí    }
   101‚Üí
   102‚Üí    spans.set(node.column, { openY, endY });
   103‚Üí  });
   104‚Üí
   105‚Üí  return spans;
   106‚Üí}
   107‚Üí
   108‚Üí// ‚îÄ‚îÄ‚îÄ SVG Connector Layer ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ
   109‚Üí
   110‚Üíinterface ConnectorProps {
   111‚Üí  flat: FlatNode[];
   112‚Üí  laneSpans: Map<number, LaneSpan>;
   113‚Üí  totalRows: number;
   114‚Üí  svgWidth: number;
   115‚Üí}
   116‚Üí
   117‚Üífunction ConnectorLayer({ flat, laneSpans, totalRows, svgWidth }: ConnectorProps) {
   118‚Üí  const elems: ReactElement[] = [];
   119‚Üí  const rowById = new Map<string, number>(flat.map((n, i) => [n.id, i]));
   120‚Üí
   121‚Üí  // Vertical lane lines
   122‚Üí  laneSpans.forEach((span, col) => {
   123‚Üí    const x = colX(col);
   124‚Üí    if (span.endY > span.openY) {
   125‚Üí      elems.push(
   126‚Üí        <line
   127‚Üí          key={`lane-${col}`}
   128‚Üí          x1={x}
   129‚Üí          y1={span.openY}
   130‚Üí          x2={x}
   131‚Üí          y2={span.endY}
   132‚Üí          stroke="var(--color-border-default)"
   133‚Üí          strokeWidth="1.5"
   134‚Üí        />,
   135‚Üí      );
   136‚Üí    }
   137‚Üí  });
   138‚Üí
   139‚Üí  // S-curve connectors for branches
   140‚Üí  flat.forEach((node) => {
   141‚Üí    if (!node.isNewBranch || !node.parentId) return;
   142‚Üí    const parentRow = rowById.get(node.parentId);
   143‚Üí    if (parentRow === undefined) return;
   144‚Üí
   145‚Üí    const parentNode = flat[parentRow];
   146‚Üí    const px = colX(parentNode.column);
   147‚Üí    const py = rowY(parentRow);
   148‚Üí    const cx = colX(node.column);
   149‚Üí    const dx = Math.abs(cx - px);
   150‚Üí    const curveH = curveHeight(dx);
   151‚Üí    const endY = py + curveH;
   152‚Üí    const midY = py + curveH / 2;
   153‚Üí    const d = `M ${px} ${py} C ${px} ${midY} ${cx} ${midY} ${cx} ${endY}`;
   154‚Üí    elems.push(
   155‚Üí      <path
   156‚Üí        key={`scurve-${node.id}`}
   157‚Üí        d={d}
   158‚Üí        fill="none"
   159‚Üí        stroke="var(--color-border-default)"
   160‚Üí        strokeWidth="1.5"
   161‚Üí      />,
   162‚Üí    );
   163‚Üí  });
   164‚Üí
   165‚Üí  return (
   166‚Üí    <svg
   167‚Üí      style={{
   168‚Üí        position: 'absolute',
   169‚Üí        top: 0,
   170‚Üí        left: 0,
   171‚Üí        width: svgWidth,
   172‚Üí        height: totalRows * ROW_HEIGHT,
   173‚Üí        pointerEvents: 'none',
   174‚Üí        zIndex: 1,
   175‚Üí      }}
   176‚Üí    >
   177‚Üí      {elems}
   178‚Üí    </svg>
   179‚Üí  );
   180‚Üí}
   181‚Üí
   182‚Üí// ‚îÄ‚îÄ‚îÄ Formatting Helpers ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ
   183‚Üí
   184‚Üífunction formatDuration(ms: number): string {
   185‚Üí  if (ms < 1000) return `${ms}ms`;
   186‚Üí  return `${(ms / 1000).toFixed(1)}s`;
   187‚Üí}
   188‚Üí
   189‚Üífunction getMessageSummary(message: Message | undefined): string {
   190‚Üí  if (!message) return '-';
   191‚Üí  const content = message.content || '';
   192‚Üí  if (!content) return '-';
   193‚Üí  return content.slice(0, 200);
   194‚Üí}
   195‚Üí
   196‚Üífunction formatRole(role: string): string {
   197‚Üí  switch (role) {
   198‚Üí    case 'user': return 'user';
   199‚Üí    case 'assistant': return 'assistant';
   200‚Üí    case 'system': return 'system';
   201‚Üí    case 'tool_use': return 'tool_use';
   202‚Üí    case 'tool_result': return 'tool_result';
   203‚Üí    default: return role;
   204‚Üí  }
   205‚Üí}
   206‚Üí
   207‚Üí// ‚îÄ‚îÄ‚îÄ Single Row ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ
   208‚Üí
   209‚Üíinterface GraphRowProps {
   210‚Üí  node: FlatNode;
   211‚Üí  svgWidth: number;
   212‚Üí  isSelected: boolean;
   213‚Üí  onClick: () => void;
   214‚Üí  getMessage: (id: string) => Message | undefined;
