   217‚Üífunction GraphRow({ node, svgWidth, isSelected, onClick, getMessage }: GraphRowProps) {
   218‚Üí  const cx = colX(node.column);
   219‚Üí  const cy = ROW_HEIGHT / 2;
   220‚Üí  const { request } = node;
   221‚Üí
   222‚Üí  // Get the last message from request_messages
   223‚Üí  const lastMessageId = request.request_messages[request.request_messages.length - 1];
   224‚Üí  const lastMessage = lastMessageId ? getMessage(lastMessageId) : undefined;
   225‚Üí  const summary = getMessageSummary(lastMessage);
   226‚Üí  const messageType = lastMessage ? formatRole(lastMessage.role) : '-';
   227‚Üí
   228‚Üí  return (
   229‚Üí    <button
   230‚Üí      onClick={onClick}
