   246‚Üí            left: 0,
   247‚Üí            top: 0,
   248‚Üí            bottom: 0,
   249‚Üí            width: 2,
   250‚Üí            background: 'var(--color-border-accent)',
   251‚Üí          }}
   252‚Üí        />
   253‚Üí      )}
   254‚Üí
   255‚Üí      {/* Graph lane area */}
   256‚Üí      <div
   257‚Üí        style={{
   258‚Üí          position: 'relative',
   259‚Üí          width: svgWidth,
   260‚Üí          flexShrink: 0,
   261‚Üí          height: '100%',
   262‚Üí          zIndex: 2,
   263‚Üí        }}
   264‚Üí      >
   265‚Üí        <svg
   266‚Üí          style={{
   267‚Üí            position: 'absolute',
   268‚Üí            top: 0,
   269‚Üí            left: 0,
   270‚Üí            width: svgWidth,
   271‚Üí            height: ROW_HEIGHT,
   272‚Üí            pointerEvents: 'none',
