   277‚Üí            cy={cy}
   278‚Üí            r={NODE_R}
   279‚Üí            fill={isSelected ? 'var(--color-border-accent)' : 'var(--color-bg-primary)'}
   280‚Üí            stroke="var(--color-border-accent)"
   281‚Üí            strokeWidth="2"
   282‚Üí          />
   283‚Üí        </svg>
   284‚Üí      </div>
   285‚Üí
   286‚Üí      {/* Request info */}
   287‚Üí      <div
   288‚Üí        style={{
   289‚Üí          display: 'flex',
   290‚Üí          flexDirection: 'column',
   291‚Üí          paddingLeft: 8,
